mapper.select:
SELECT * FROM tbl_writing
WHERE wr_user = #{u_seq}
ORDER BY wr_last_date DESC

mapper.update:
 		UPDATE tbl_writing 
		SET wr_like_count = wr_like_count + 1
		WHERE wr_seq = #{wr_seq};

 		UPDATE tbl_writing
		SET wr_like_count = wr_like_count - 1
		WHERE wr_seq = #{wr_seq};

UPDATE tbl_writing
SET wr_warning_count = wr_warning_count + 1
WHERE wr_seq = #{wr_seq};

UPDATE tbl_writing
SET wr_warning_count = wr_warning_count - 1
WHERE wr_seq = 	#{wr_seq};

UPDATE tbl_writing SET
wr_content = #{wr_content},
wr_origin = #{wr_origin}
WHERE wr_seq = #{wr_seq}
